			const rect = image.getBoundingClientRect(),
				x = event.clientX - rect.left,
				y = event.clientY - rect.top;

			image?.style.setProperty('--cursor-x', `${x}px`);
			image?.style.setProperty('--cursor-y', `${y}px`);
		}
	};
